import { AutoreplyMode } from './core/autoreply-mode';
import { PrinterAlignment } from './core/printer-alignment';
import { Ticket } from './models/ticket.model';
import { Observable } from "rxjs";
export default class Printer {
    private handle;
    constructor(dev: string, mode?: AutoreplyMode);
    printText(text: string): void;
    feedLine(numLines: number): void;
    getPrinterStatus(timeout: number): any;
    cutPaper(fullCut?: boolean): any;
    feedAndHalfCutPaper(): any;
    close(): any;
    reset(): any;
    clear(): any;
    version(): any;
    printTestPage(): any;
    /**
     * Set print alignment
     *
     * @param nAlignment print alignment, value are defined as follow:
     *      - 0 -> align left
     *      - 1 ->  align center
     *      - 2 ->  align right
     */
    setPrintAlignment(nAlignment: PrinterAlignment): any;
    printTicket(ticket: Ticket, clear?: boolean): Promise<unknown>;
    printTestTicket(): void;
    getPrinterInfo(): any;
    onStatusChange(): Observable<unknown>;
}
