#include <stdio.h>

#include "autoreplyprint.h"

void onOpened(void *handle, const char *name, void *private_data) {
    printf("name: %s, data: %s\n", name, (char *)private_data);
}

int main(int argc, char *argv[]) {
    puts("Start");
    CP_Port_AddOnPortOpenedEvent(onOpened, "ciao");
    void *h = CP_Port_OpenUsb("/dev/usb/lp1", 0);
    CP_Pos_PrintText(h, "Ciao");
    CP_Pos_FeedLine(h, 3);
    CP_Pos_HalfCutPaper(h);
}
